select
from store_clothes c INNER JOIN (SELECT o.* from store_order o where o.order_status='5') o on c.order_code =o.order_code LEFT JOIN store s on c.store_id =s.id LEFT JOIN store_data_dictionary d on c.clothes_name =d.id LEFT JOIN store_data_dictionary d1 on c.color =d1.id LEFT JOIN store_data_dictionary d2 on c.brand =d2.id LEFT JOIN member m on c.member_id =m.id
LEFT JOIN store_hand_on_area a on c.hand_on_area =a.id and a.hand_type='1' LEFT JOIN store_hand_on_no n on c.hand_on_no =n.id and n.hand_type='1' LEFT JOIN store_clothes_flow cf on c.id =cf.clothes_id and cf.clothes_status='15' LEFT JOIN store_hand_on_area fa on c.factory_hand_on_area =fa.id and fa.hand_type='2' LEFT JOIN store_hand_on_no fn on c.factory_hand_on_no =fn.id and fn.hand_type='2' LEFT JOIN store_clothes_flow cf1 on c.id =cf1.clothes_id and cf1.clothes_status='16'
LEFT JOIN send_washing_detail wd on c.id =wd.clothes_id LEFT JOIN send_washing w on w.send_number =wd.send_number LEFT JOIN leave_factory_detail fd on c.id =fd.clothes_id LEFT JOIN leave_factory lf on fd.leave_factory_number =lf.leave_factory_number where 1=1
and c.bar_code = #{barCode}
and c.store_id = #{storeId}
and c.status = #{status}
and c.order_code = #{orderCode}
and (c.member_id = #{queryKey} || c.mobile_phone =#{queryKey})
order by c.create_date desc

select c.id, c.order_code, c.bar_code, c.urgency, c.member_id, c.card_number, c.service_type, c.attachment, c.flaw, c.mobile_phone, c.telephone,
c.price, c.status, c.service_fee, c.create_date, c.create_user_id, c.create_user_name, c.update_date, c.update_user_name, c.update_user_id, c.store_id, s.store_name, c.factory_hand_on_area as factoryHandOnArea, c.factory_hand_on_no as factoryHandOnNo, o.clothes_count
from store_clothes c LEFT JOIN store s on c.store_id =s.id LEFT JOIN store_order o on c.order_code =o.order_code where 1=1
and c.bar_code = #{barCode}
and c.store_id = #{storeId}
and c.status = #{status}
and c.order_code = #{orderCode}
and (c.member_id = #{queryKey} || c.mobile_phone =#{queryKey})
order by c.create_date desc

select c.id, c.order_code, c.bar_code, m.name as user_name, d.data_name as clothes_name, e.data_name as color, c.price, c.brand, c.flaw, c.status, c.service_fee, c.create_date, c.create_user_name, c.store_id, s.store_name, s.telephone, o.order_address from store_clothes c LEFT JOIN store s on c.store_id =s.id LEFT JOIN store_order o on c.order_code =o.order_code LEFT JOIN member m ON c.member_id = m.id LEFT JOIN store_data_dictionary d ON c.clothes_name = d.id LEFT JOIN store_data_dictionary e ON c.color = e.id where 1=1
and c.order_code = #{orderCode}
order by c.create_date desc

select
from send_washing_detail wd LEFT JOIN send_washing w ON wd.send_number = w.send_number LEFT JOIN store_clothes c ON wd.clothes_id = c.id
LEFT JOIN store_hand_on_area a on c.hand_on_area =a.id and a.hand_type='1' LEFT JOIN store_hand_on_no n on c.hand_on_no =n.id and n.hand_type='1'
LEFT JOIN store_hand_on_area fa on c.factory_hand_on_area =fa.id and fa.hand_type='2' LEFT JOIN store_hand_on_no fn on c.factory_hand_on_no =fn.id and fn.hand_type='2'
LEFT JOIN store_order o on c.order_code =o.order_code LEFT JOIN leave_factory_detail fd on c.id =fd.clothes_id LEFT JOIN leave_factory lf on fd.leave_factory_number =lf.leave_factory_number LEFT JOIN store_clothes_flow cf on c.id =cf.clothes_id and cf.clothes_status='15' LEFT JOIN store_clothes_flow cf1 on c.id =cf.clothes_id and cf1.clothes_status='16' LEFT JOIN store s ON c.store_id = s.id LEFT JOIN store_data_dictionary d ON c.clothes_name = d.id LEFT JOIN store_data_dictionary d1 ON c.color = d1.id LEFT JOIN store_data_dictionary d2 ON c.brand = d2.id LEFT JOIN store_data_dictionary d3 ON c.flaw = d3.id LEFT JOIN member m ON c.member_id = m.id where 1=1
and c.bar_code = #{barCode}
and c.store_id = #{storeId}
and c.status = #{status}
and wd.send_number = #{sendNumber}
and (c.member_id = #{queryKey} || c.mobile_phone =#{queryKey})
order by c.create_date desc

SELECT c.id, c.order_code, c.bar_code, c.urgency, c.member_id, m.name as member_name, c.card_number, d.data_name as clothes_name, c.service_type, d1.data_name as color, d2.data_name as brand, c.mobile_phone, c.telephone, c.price, c.clothes_status as status, c.service_fee, c.create_date, c.create_user_id, c.create_user_name, c.update_date, c.update_user_name, c.update_user_id, c.store_id, s.store_name from ( SELECT sc.*,scf.opt_user_id as opt_user_id,sc.status as clothes_status from store_clothes_flow scf INNER JOIN store_clothes sc on scf.clothes_id =sc.id where scf.clothes_status=#{clothesStatus} ) c LEFT JOIN store s on c.store_id =s.id LEFT JOIN store_order o on c.order_code =o.order_code LEFT JOIN member m on c.member_id =m.id LEFT JOIN store_data_dictionary d on c.clothes_name =d.id LEFT JOIN store_data_dictionary d1 on c.color =d1.id LEFT JOIN store_data_dictionary d2 on c.brand =d2.id where 1=1
and c.opt_user_id = #{userId}
and c.bar_code = #{barCode}
order by c.create_date desc
